'use strict'
let _ = require('lodash')
let EVT = require('evrythng-extended')
let fs = require('fs')
let moment = require('moment')
let request = require('request-promise')
let sleep = require('sleep')
let test = require('blue-tape')

let BASE_DIR = `${__dirname}/..`
let PREFIX = 'REACTOR'

let settings = require(`${BASE_DIR}/uploadSettings.json`)

let operator = new EVT.Operator(settings.operatorKey)

function getSampleRule () {
    let rule = JSON.parse(fs.readFileSync(`${BASE_DIR}/tests/shared/rules-sample-1.json`, 'utf8'))
    rule.name = `${PREFIX} Rule`
    return rule
}

function log (objectType, verb, obj) {
    console.log(`${objectType} '${obj.name}' ${verb} with ID '${obj.id}'`)
}

async function createProject () {
    let project = await operator.project().create({
        'name': `${PREFIX} Project`
    })

    log('Project', 'created', project)
    return operator.project(project.id)
}

async function createApp (project) {
    let app = await project.application().create({
        'name': `${PREFIX} Application`,
        'socialNetworks': {},
    })

    log('Application', 'created', app)
    return new EVT.App(app.appApiKey)
}

async function fetchProject (settings) {
    let project = await operator
        .project(settings.projectId)
        .read()

    log('Project', 'fetched', project)
    return operator.project(settings.projectId)
}

async function fetchApp (settings) {
    let app = await operator
        .project(settings.projectId)
        .application(settings.applicationId)
        .read()

    log('Application', 'fetched', app)
    return new EVT.App(app.appApiKey)
}

async function createUser (app) {
    let user = await app.appUser().create({ anonymous: true })
    log('User', 'created', user)
    return new EVT.User(user, app)
}

async function createCollection (user) {
    let collection = await user.collection().create({
        'name': `${PREFIX} Collection`,
    })

    log('Collection', 'created', collection)
    return collection
}

async function createThng (user, rules, properties) {
    let thng = await user.thng().create({
        'name': `${PREFIX} Thng`,
        'properties': _.assign(properties, rules),
    })

    log('Thng', 'created', thng)
    return user.thng(thng.id)
}

async function createActionTypes (names) {
    let actionTypes = Promise.all(names.map(async (name) => {
        try {
            let actionType = await operator.actionType().create({ name })
            // TODO: rescope action types

            log('ActionType', 'created', actionType)

        } catch (error) {
            if (error.status !== 409)
                console.error(JSON.stringify(error))
        }
    }))
}

async function readReactorStatus (appData) {
    console.log(
        `Getting the REACTOR status for App '${appData.name}' with ID '${appData.id}'...`)

    let status = await request.get({
        url: `https://api.evrythng.com/${appData.resource.path}/reactorScript/status`,
        headers: {
            'Accept': 'application/json',
            'Authorization': settings.operatorKey,
        }
    })

    console.log(`REACTOR status: ${status}`)
    return status
}

async function deleteAllActions (user) {
    await user.action('all').read().then((actions) => {

        return Promise.all(_.map(actions, (action) => {
            return operator.action(action.type, action.id).delete()

        })).catch((error) => {
            console.log(`Error deleting Actions: ${JSON.stringify(error, null, 2)}`)
        })
    })

    console.log('All Actions deleted')
}

async function setupFixtures () {
    //let project = await createProject(operator)
    //let app = await createApp(project)
    let project = await fetchProject(settings)
    let app = await fetchApp(settings)
    let user = await createUser(app)
    await deleteAllActions(user)
    let collection = await createCollection(user)
    await createActionTypes(['_dim', '_on', '_off'])

    return { project, app, user, collection }
}

async function tearDown (fixtures) {
    await deleteAllActions(fixtures.user)
}

async function test1 (t) {
    let { project, app, user, collection } = await setupFixtures()
    let rule = getSampleRule()
    rule.conditions = [
        {
            'variable': 'occupancy_detected',
            'operator': '=',
            'value': true,
            'logical_operator': 'and',
        },
        {
            'variable': 'ambient_light',
            'operator': '>',
            'value': 10,
            'logical_operator': null,
        },
    ]
    rule.operations = [
        {
            'collections': [
                collection.id
            ],
            'actions': [
                {
                    'name': '_off'
                },
                {
                    'name': '_dim',
                    'parameters': {
                        'value': 1
                    }
                }
            ]
        }
    ]

    let rules = { '__rule__7828728700101': rule }

    let properties = {
        'occupancy_detected': false,
        'ambient_light': 9,
    }

    let actions = await user.action('all').read()
    t.equal(actions.length, 0)

    let thng = await createThng(user, rules, properties)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 0)

    let props = await thng.property('ambient_light').update(11)
    let ambient_light = props[0].value
    t.equal(ambient_light, 11)

    props = await thng.property('occupancy_detected').update(true)
    let occupancy_detected = props[0].value
    t.equal(occupancy_detected, true)

    sleep.sleep(1)

    actions = await user.action('all').read()

    t.equal(actions.length, 2)

    await tearDown({ user })
}

async function test2 (t) {
    let { project, app, user, collection } = await setupFixtures()
    let rule = getSampleRule()
    let today = moment.utc().format('ddd').toLowerCase()
    console.log(today)
    rule.conditions = [
        {
            'variable': 'day_of_week',
            'operator': '=',
            'value': [
                today
            ],
            'logical_operator': 'and',
        },
        {
            'variable': 'occupancy_detected',
            'operator': '=',
            'value': false,
            'logical_operator': null,
        },
    ]
    rule.operations = [
        {
            'collections': [
                collection.id
            ],
            'actions': [
                {
                    'name': '_dim',
                    'parameters': {
                        'value': 5
                    }
                },
            ]
        }
    ]

    let rules = { '__rule__7828728700101': rule }

    let properties = {
        'occupancy_detected': false,
        'colour_temperature': 3,
    }

    let actions = await user.action('all').read()
    t.equal(actions.length, 0)

    let thng = await createThng(user, rules, properties)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 1)
    t.equal(actions[0].type, '_dim')
    t.equal(actions[0].customFields.value, 5)

    // This update should, since we've waited.
    let props = await thng.property('colour_temperature').update(4)
    let colour_temperature = props[0].value
    t.equal(colour_temperature, 4)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 2)

    await tearDown({ user })
}

async function test3 (t) {
    let { project, app, user, collection } = await setupFixtures()
    let rule = getSampleRule()
    let soon = moment.utc().add(5, 'seconds').format('HH:mm:ss')
    rule.conditions = [
        {
            'variable': 'time_of_day',
            'operator': '>',
            'value': soon,
            'logical_operator': null,
        },
    ]
    rule.operations = [
        {
            'collections': [
                collection.id
            ],
            'actions': [
                {
                    'name': '_on'
                },
            ]
        }
    ]

    let rules = { '__rule__7828728700101': rule }

    let properties = {
        'ambient_light': 1,
    }

    let actions = await user.action('all').read()
    t.equal(actions.length, 0)

    let thng = await createThng(user, rules, properties)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 0)

    // This update shouldn't trigger an action, since it's not time yet.
    let props = await thng.property('ambient_light').update(2)
    let ambient_light = props[0].value
    t.equal(ambient_light, 2)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 0)

    sleep.sleep(3)

    // This update should, since we've waited.
    props = await thng.property('ambient_light').update(3)
    ambient_light = props[0].value
    t.equal(ambient_light, 3)

    sleep.sleep(1)
    actions = await user.action('all').read()
    t.equal(actions.length, 1)
    t.equal(actions.length, 1)
    t.equal(actions[0].type, '_on')

    await tearDown({ user })
}

test('Integration tests', async (t) => {
    await test1(t)
    await test2(t)
    await test3(t)
})

